import { Link } from "react-router-dom";

const Post = ({post}) =>{
    return (<article>

        <p>{post.text}</p>
        {post.image ? (
        <img
        src={`${process.env.REACT_APP_BACKEND}/uploads/${post.image}`}
        alt={post.text}
        />
    ) : null}
    <p>
        By <Link to={`/user/${post.user_id}`}>{post.text}</Link> on{" "}
        <Link to={`/post/${post.id}`}>
          {new Date(post.created_at).toLocaleString()}
        </Link>
      </p>

    </article>);
};

export default Post;
